import pdfMake from 'pdfmake/build/pdfmake'
import vfsFonts from 'pdfmake/build/vfs_fonts'
export default (assessment) => {
  const {
    assessor,
    make,
    model,
    color,
    name,
    phone,
    email,
    isRefurbish,
    parts,
    pickupDate,
    serviceItems,
    temporaryBike,
    totalCost,
    urgent,
    comment,
  } = assessment

  capitalize = function (str) {
    if (!str) return
    return str
      .toLowerCase()
      .split(' ')
      .map((x) => x[0].toUpperCase() + x.slice(1))
      .join(' ')
  }

  const serviceItemNames = serviceItems.map((item) => {
    return [
      {
        text: item.name,
        align: 'right',
        colSpan: 3,
      },
      {},
      {},
      {
        image: item.code ? item.code : 'O',
        width: 60,
        height: 21,
        alignment: 'center',
      },
    ]
  })

  const mergedParts = parts.partsItem.reduce((acc, val) => {
    const itemName = val.name.replace(/^front\s+|rear\s+/gi, '')
    if (acc[itemName]) {
      const codes = [acc[itemName].code, val.code]
      if (codes.includes('F') && codes.includes('R')) {
        acc[itemName].code = 'FR'
      }
    } else {
      acc[itemName] = val
    }
    return acc
  }, {})
  const servicePartNames = Object.keys(mergedParts).map((name) => {
    const item = mergedParts[name]
    if (!item.name) {
      return ['There are no parts for this service', '']
    } else {
      return [
        {
          text: name,
          alignment: 'right',
          colSpan: 3,
        },
        {},
        {},
        {
          image: item.code,
          width: 60,
          height: 21,
          alignment: 'center',
        },
      ]
    }
  })
  const tempBike = temporaryBike ? 'A temporary bike has been provided to this customer.' : ''
  const isUrgent = urgent
    ? `URGENT: This request must be completed by ${pickupDate.toDateString()}`
    : `Pickup Date: ${pickupDate.toDateString()}`
  const bikeModel = model ? ` - ${model}` : ''

  var docDefinition = {
    pageSize: 'A4',

    watermark: { text: 'Refurb', color: '#8FDBB6', opacity: 0.3, bold: true },

    images: {
      R:
        'data:image/png;base64,',
      F:
        'data:image/png;base64,',
      FR:
        'data:image/jpeg;base64,',
      O:
        'data:image/png;base64,',
    },

    content: [
      {
        text: `${capitalize(make)} ${bikeModel} - ${capitalize(color)} - Total Price $${totalCost / 100}`,
        style: 'subheader',
        fontSize: 20,
      },
      {
        text: `Owner:   ${capitalize(name ? name : 'Refurbish')}     email: ${email ? email : 'N/A'}     Ph: ${
          phone ? phone : 'N/A'
        }`,
      },

      { text: `Assessor: ${assessor} `, style: 'text' },
      { text: `${isUrgent} `, style: 'text', bold: true },
      { text: '', style: 'text' },

      {
        table: {
          widths: [240, 80, 80, 80],
          heights: 18,
          headerRows: 2,
          // keepWithHeaderRows: 1,
          body: [
            [
              {
                text: services.baseService,
                style: 'tableHeader',
                colSpan: 3,
                alignment: 'center',
              },
              {},
              {},
              {
                text: 'Done?',
                alignment: 'center',
              },
            ],
            ...serviceItemNames,
            [{ text: '', style: 'tableHeader', colSpan: 4, alignment: 'center' }, {}, {}, {}],
            [
              { text: 'Other Items', style: 'tableHeader', alignment: 'center', colSpan: 3 },
              {},
              {},
              { text: 'Done?', alignment: 'center' },
            ],
            ...servicePartNames,
            [{ text: '', style: 'tableHeader', colSpan: 3, alignment: 'center' }, {}, {}, {}],
            [{ text: 'Notes', style: 'tableHeader', colSpan: 4, alignment: 'center' }, {}, {}, {}],
            [{ text: comment, colSpan: 4 }, '', '', ''],
          ],
        },
      },
    ],

    styles: {
      header: {
        fontSize: 24,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 18,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      text: {
        fontSize: 12,
        margin: [5, 5, 5, 5],
      },
      servicesTable: {
        margin: [5, 5, 5, 15],
      },
      tableHeader: {
        bold: true,
        fontSize: 15,
        color: 'black',
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  }
  if (tempBike) {
    docDefinition.content.push({ text: tempBike })
  }
  if (!isRefurbish) {
    docDefinition.watermark.text = name
    docDefinition.watermark.color = 'cyan'
  }
  try {
    pdfMake.createPdf(docDefinition).open()
  } catch (error) {
    alert('There was a problem creating the printable job card: ' + error.message)
  }
}
